import{_ as r}from"./plugin-vue_export-helper-c27b6911.js";import{r as s,o as c,c as p,d as e,e as n,a,f as d}from"./app-0927109a.js";const o={},i={href:"https://www.captainai.net/",target:"_blank",rel:"noopener noreferrer"},h={href:"https://captainbed.vip/",target:"_blank",rel:"noopener noreferrer"},b=d('<h2 id="神经网络" tabindex="-1"><a class="header-anchor" href="#神经网络" aria-hidden="true">#</a> 神经网络</h2><p>神经网络是一种人工智能方法，用于教计算机以受人脑启发的方式处理数据。这是一种机器学习过程，称为深度学习，它使用类似于人脑的分层结构中的互连节点或神经元。它可以创建自适应系统，计算机使用该系统来从错误中进行学习并不断改进。因此，人工神经网络可以尝试解决复杂的问题，例如更准确地总结文档或人脸识别。</p><p>人工神经网络是受到人类大脑结构的启发而创造出来的，这也是它能拥有真智能的根本原因。在我们的大脑中，有数十亿个称为神经元的细胞，它们连接成了一个神经网络。</p><p>人工神经元也有相似的工作原理。如下图所示。</p><img src="data:image/png;base64,"><p>上面的x是神经元的输入，相当于树突接收的多个外部刺激。w是每个输入对应的权重，它影响着每个输入x的刺激强度。</p><p>大脑的结构越简单，那么智商就越低。单细胞生物是智商最低的了。人工神经网络也是一样的，网络越复杂它就越强大，所以我们需要深度神经网络。这里的深度是指层数多，层数越多那么构造的神经网络就越复杂。</p><h2 id="机器学习" tabindex="-1"><a class="header-anchor" href="#机器学习" aria-hidden="true">#</a> 机器学习</h2><h3 id="附件" tabindex="-1"><a class="header-anchor" href="#附件" aria-hidden="true">#</a> 附件</h3>',9),q=["href"];function W(t,V){const A=s("ExternalLinkIcon");return c(),p("div",null,[e("p",null,[n("床长人工智能教程："),e("a",i,[n("https://www.captainai.net/"),a(A)])]),e("p",null,[n("会员入口："),e("a",h,[n("https://captainbed.vip/"),a(A)])]),b,e("p",null,[e("a",{href:t.$withBase("/files/人工智能/PRML中文版.pdf"),download:""},"PRML中文版.pdf",8,q)])])}const L=r(o,[["render",W],["__file","index.html.vue"]]);export{L as default};
