import{_ as t}from"./plugin-vue_export-helper-c27b6911.js";import{r as l,o as s,c,d as e,e as n,a as _}from"./app-524c53f5.js";const r={},a=e("p",null,"在《分布式系统原理与范型》一书中有如下定义：“分布式系统是若干独立计算机的集合，这些计算机对于用户来说就像单个相关系统”；",-1),d=e("p",null,"分布式系统是由一组通过网络进行通信、为了完成共同的任务而协调工作的计算机节点组成的系统。分布式系统的出现是为了用廉价的、普通的机器完成单个计算机无法完成的计算、存储任务。其目的是利用更多的机器，处理更多的数据。",-1),i=e("p",null,"分布式系统（distributed system）是建立在网络之上的软件系统。",-1),u=e("p",null,"首先需要明确的是，只有当单个节点的处理能力无法满足日益增长的计算、存储任务的时候，且硬件的提升（加内存、加磁盘、使用更好的CPU）高昂到得不偿失的时候，应用程序也不能进一步优化的时候，我们才需要考虑分布式系统。",-1),p=e("p",null,"因为，分布式系统要解决的问题本身就是和单机系统一样的，而由于分布式系统多节点、通过网络通信的拓扑结构，会引入很多单机系统没有的问题，为了解决这些问题又会引入更多的机制、协议，带来更多的问题。",-1),h=e("hr",null,null,-1),m=e("p",null,"RPC（Remote Procedure Call）是指远程过程调用，是一种进程间通信方式，他是一种技术的思想，而不是规范。它允许程序调用另一个地址空间（通常是共享网络的另一台机器上）的过程或函数，而不用程序员显式编码这个远程调用的细节。即程序员无论是调用本地的还是远程的函数，本质上编写的调用代码基本相同。",-1),f=e("p",null,"也就是说两台服务器A，B，一个应用部署在A服务器上，想要调用B服务器上应用提供的函数/方法，由于不在一个内存空间，不能直接调用，需要通过网络来表达调用的语义和传达调用的数据。为什么要用RPC呢？就是无法在一个进程内，甚至一个计算机内通过本地调用的方式完成的需求，比如不同的系统间的通讯，甚至不同的组织间的通讯，由于计算能力需要横向扩展，需要在多台机器组成的集群上部署应用。RPC就是要像调用本地的函数一样去调远程函数；",-1),x=e("p",null,"相关文章：",-1),C={href:"https://www.jianshu.com/p/2accc2840a1b",target:"_blank",rel:"noopener noreferrer"},w=e("p",null,[n("RPC的两个核心模块："),e("code",null,"通信"),n(" 和 "),e("code",null,"序列化")],-1);function P(k,B){const o=l("ExternalLinkIcon");return s(),c("div",null,[a,d,i,u,p,h,m,f,x,e("ul",null,[e("li",null,[e("a",C,[n("https://www.jianshu.com/p/2accc2840a1b"),_(o)])])]),w])}const v=t(r,[["render",P],["__file","index.html.vue"]]);export{v as default};
