            <span class="token literal-property property">template</span><span class="token operator">:</span><span class="token string">'&lt;li>{{index}}---{{item}}&amp;nbsp;&lt;button @click="remove">删除&lt;/button>&lt;/li>'</span><span class="token punctuation">,</span>
            <span class="token literal-property property">methods</span><span class="token operator">:</span><span class="token punctuation">{</span>
